svg.script:
// Tu código JavaScript para generar el SVG dinámico
const repoOwner = "luisespino";
const repoName = "data-structures";

async function fetchLanguages() {
  const res = await fetch(`https://api.github.com/repos/${repoOwner}/${repoName}/languages`);
  const data = await res.json();
  const total = Object.values(data).reduce((a, b) => a + b, 0);
  const filtered = Object.entries(data)
    .filter(([lang]) => !["HTML"].includes(lang))
    .sort((a, b) => b[1] - a[1])
    .slice(0, 10);

  let svgInner = '';
  let accumulated = 0;
  filtered.forEach(([lang, bytes]) => {
    const percent = (bytes / total) * 100;
    const color = {
      JavaScript: "#f1e05a",
      Python: "#3572A5",
      Java: "#b07219",
      C: "#555555",
      "C++": "#f34b7d",
      Shell: "#89e051",
      CSS: "#563d7c",
      Other: "#cccccc"
    }[lang] || "#cccccc";

    svgInner += ``;
    accumulated += percent;
  });

  // Actualiza el SVG con los datos
  document.querySelector("svg").innerHTML += svgInner;
}

fetchLanguages();
